INSERT INTO `clear_pages` VALUES(12, 'Resultado 2', '<table border="0" cellpadding="0" cellspacing="0" height="240" width="590">\n	<tbody>\n		<tr>\n			<td style="width: 151px; text-align: center; vertical-align: bottom;">\n				<p>\n					&nbsp;</p>\n				<p>\n					<br />\n					<br />\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					<img alt="" height="90" src="/socialpromos3/public/uploads/images/Quiz/start_Bohemian.png" style="float: right;" width="90" /></p>\n			</td>\n			<td style="width: 34px;">\n				&nbsp;</td>\n			<td style="width: 405px;">\n				<h2>\n					<span style="color:#cc0099;">ERES UNA:</span><br />\n					BELLEZA BOHEMIA</h2>\n				<p style="text-align: justify;">\n					<span style="color:#cc0099;">Eres un verdadero esp&iacute;ritu libre con un estilo relax y cool que se extiende a todo lo que haces. Tu vibra sexy te da un aire ex&oacute;tico que te lleva desde fiestas en terrazas hasta cenas de medianoche con tus amigas.</span><br />\n					<br />\n					Tu cabello tiende a ser un poco incontrolable. Necesitas nutrir tu cuero cabelludo con nuestra formula enriquecida que limpia y nutre el cabello que necesita control para el &ldquo;frizz&rdquo;.</p>\n				<h2>\n					TU COLECCI&Oacute;N A LA MEDIDA</h2>\n				<p>\n					{coleccion}</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n', 'resultado2');
INSERT INTO `clear_pages` VALUES(13, 'Resultado 3', '<table border="0" cellpadding="0" cellspacing="0" height="257" width="590">\n	<tbody>\n		<tr>\n			<td style="width: 151px; text-align: center;">\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					<br />\n					&nbsp;</p>\n				<p>\n					<img alt="" src="/socialpromos3/public/uploads/images/Quiz/start_Classic.png" style="float: right; width: 90px; height: 90px;" /></p>\n			</td>\n			<td style="width: 34px; text-align: center;">\n				&nbsp;</td>\n			<td style="width: 405px;">\n				<h2>\n					<span style="color:#cc0099;">ERES UNA:</span><br />\n					CHICA CL&Aacute;SICA</h2>\n				<p style="text-align: justify;">\n					<span style="color:#cc0099;">Dulce y sofisticada, el ejemplo perfecto de elegancia casual. Coqueteas con la moda pero te quedas con los cl&aacute;sicos. Eres cool y segura de ti misma, no importa si est&aacute;s en una apertura chic o en una degustaci&oacute;n de vino con las chicas.</span><br />\n					<br />\n					Tu cabello es impactante y bello, as&iacute; que no dejes que la resequedad te venza. Nutre tu cuero cabelludo. Esa dosis extra de humectantes es exactamente lo que necesitas para aliviar tu cuero cabelludo.</p>\n				<h2>\n					TU COLECCI&Oacute;N A LA MEDIDA</h2>\n				<p>\n					{coleccion}</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n', 'resultado3');
INSERT INTO `clear_pages` VALUES(14, 'Resultado 4', '<table border="0" cellpadding="0" cellspacing="0" height="270" width="590">\n	<tbody>\n		<tr>\n			<td style="width: 151px; text-align: center; vertical-align: bottom;">\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					&nbsp;</p>\n				<p>\n					<img alt="" height="90" src="/socialpromos3/public/uploads/images/Quiz/start_Boyfriend.png" style="float: right;" width="90" /></p>\n			</td>\n			<td style="width: 34px; text-align: center;">\n				&nbsp;</td>\n			<td style="width: 405px;">\n				<h2>\n					<span style="color:#cc0099;">ERES UNA:</span><br />\n					BELLEZA DESPAMPANANTE</h2>\n				<p style="text-align: justify;">\n					<span style="color:#cc0099;">Eres una verdadera chica rebelde que nunca le teme a tomar riesgos. &iquest;Utilizar un atuendo de cuero negro sobre un traje de dise&ntilde;ador? &iexcl;Claro! &iquest;Fiesta coctel espont&aacute;nea? - &iquest;Por qu&eacute; no? Eres de las que le robas los mahones a tu novio para usarlos y les das un estilo fabuloso.</span><br />\n					<br />\n					Eres un camale&oacute;n del color y un cabello con mucha vida y bien arreglado es lo tuyo. Pero tener una cabellera que requiere de mucho cuidado no significa que descuides tu estilo. Sigue siendo impactante y bella nutriendo tu cuero cabelludo. Cuando nutres tu cuero cabelludo, obtienes un cabello m&aacute;s fuerte.</p>\n				<h2>\n					TU COLECCI&Oacute;N A LA MEDIDA</h2>\n				<p>\n					{coleccion}</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n', 'resultado4');
